import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { buyToken, buyStockWithToken } from './redux/Store'
import './Users.css'

function Users() {
  const dispatch = useDispatch()
  const users = useSelector((state) => state.users)
  const [selectedUser, setSelectedUser] = React.useState(users[0]?.id || 1)
  const [selectedStock, setSelectedStock] = React.useState('Apple')
  const [tokenAmount, setTokenAmount] = React.useState(0)
  const [stockAmount, setStockAmount] = React.useState(0)

  const user = users.find((user) => user.id === selectedUser)

  const handleBuyToken = () => {
    if (user && user.balance >= tokenAmount) {
      dispatch(
        buyToken({
          userId: selectedUser,
          stock: selectedStock,
          amount: Number(tokenAmount),
        })
      )
      setTokenAmount(0) // Reset token amount after purchase
    } else {
      alert('Insufficient balance to buy tokens.')
    }
  }

  const handleBuyStockWithToken = () => {
    if (user) {
      const tokenBalance = user.tokens[selectedStock] || 0
      if (tokenBalance >= stockAmount) {
        dispatch(
          buyStockWithToken({
            userId: selectedUser,
            stock: selectedStock,
            amount: Number(stockAmount),
          })
        )
        setStockAmount(0) // Reset stock amount after purchase
      } else {
        alert('Insufficient tokens to buy stock.')
      }
    }
  }

  return (
    <div className="users-page">
      <select
        onChange={(e) => setSelectedUser(Number(e.target.value))}
        value={selectedUser}
      >
        {users.map((user) => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>
      {user && (
        <div className="user-box">
          <h2>{user.name}</h2>
          <p>Remaining Balance: {user.balance}</p>
          <div className="buy-token-section">
            <select onChange={(e) => setSelectedStock(e.target.value)}>
              {[
                'Apple',
                'Tesla',
                'Bitcoin',
                'Salenium',
                'Reliance',
                'Google',
                'NASDAQ',
                'Amazon',
                'Dogcoin',
                'Either',
              ].map((stock) => (
                <option key={stock} value={stock}>
                  {stock}
                </option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Token Amount"
              value={tokenAmount}
              onChange={(e) => setTokenAmount(e.target.value)}
            />
            <button onClick={handleBuyToken}>Buy Token</button>
          </div>
          <div className="buy-stock-section">
            <select onChange={(e) => setSelectedStock(e.target.value)}>
              {[
                'Apple',
                'Tesla',
                'Bitcoin',
                'Salenium',
                'Reliance',
                'Google',
                'NASDAQ',
                'Amazon',
                'Dogcoin',
                'Either',
              ].map((stock) => (
                <option key={stock} value={stock}>
                  {stock}
                </option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Stock Amount"
              value={stockAmount}
              onChange={(e) => setStockAmount(e.target.value)}
            />
            <button onClick={handleBuyStockWithToken}>
              Buy Stock with Token
            </button>
          </div>
          {/* Display user's cart or transactions here */}
        </div>
      )}
    </div>
  )
}

export default Users
