import React, { useState } from 'react'
import './Bus.css';
import Buyorsell from '../../../mini_comp/Buyrorsell/Buyorsell';
import { NavLink } from 'react-router-dom';



const Bus = () => {
    // const[ticket_details,setticket_details]=({first_name,last_name,email,journey_from,journey_to,bus_name,bus_number,bus_cont_no,ticket_number,pnr_number })= req.body;
    const [Isbuy, setIsbuy] = useState(false);

    const OnclickBuy = () => {
        setIsbuy(true);

    }
    const OnclickSell = () => {
        setIsbuy(false);
    }
    const OnClickSell=()=>{
        
    }

    
//   const save_handlesubmit = async (e) => {
//     SetEditable(false);
//     e.preventDefault();
//     const {first_name,last_name,email,journey_from,journey_to,bus_name,bus_number,bus_cont_no,ticket_number,pnr_number } = ticket_details;
//     const response = await fetch(`http://localhost:5000/api/tickets/sellbusticket/`, {

//       method: 'PUT',

//       headers: {
//         'Content-Type': 'application/json',
//         'auth-token': localStorage.getItem('token')

//       } // body data type must match "Content-Type" header
//       , body: JSON.stringify({ Username: Username, UserRole: UserRole, Country: Country, Branch: Branch, Section: Section, Mentor: Mentor, SGPA: SGPA, Internship: Internship })
//     });
//     const json = await response.json();
//     console.log(json);
//     console.log('data has been saved');
    //Savig authentication token and redirecting to Home_page
    //  if(json.success)
    //  { 
    //   localStorage.setItem('token',json.authtoken);
    //   navigate("/");
    //  } else{
    //     console.log("not saved");
    //  }

    // window.location.reload(false);
//   }
    return (
        <div id='Bus'>
            <div className='Bus'>

                <div id='buyorsell'>
                    <div className='buyorsell'>
                        <ul>
                            <li className={Isbuy ? 'active' : 'notactive'} onClick={OnclickBuy}>
                                Buy
                            </li>
                            <li className={Isbuy ? 'notactive' : 'active'} onClick={OnclickSell}>
                                Sell
                            </li>
                        </ul>
                    </div>

                    <div className={Isbuy ? 'buy_data' : 'hide'}>
                        <h1>Buy</h1>
                    </div>
                    <div className={Isbuy ? 'hide' : 'sell_data'}>
                        <p className='tagline_sell'>
                            Dont't let your ticket go waste.
                        </p>

                        <div className='sell_form'>
                            <div className="container">
                                <form action="action_page.php">
                                    <div className="row">
                                        <div className="col-25">
                                            <label for="fname">First Name</label>
                                        </div>
                                        <div className="col-75">
                                            <input type="text" id="fname" name="firstname" placeholder="Your name.." />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-25">
                                            <label for="lname">Last Name</label>
                                        </div>
                                        <div className="col-75">
                                            <input type="text" id="lname" name="lastname" placeholder="Your last name.." />
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-25">
                                            <label for="email">Email</label>
                                        </div>
                                        <div className="col-75">
                                            <input type="text" id="email" name="email" placeholder="Your email.." />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-25">
                                            <label for="jrny_date">Journey From</label>
                                        </div>
                                        <div className="col-75">
                                            <input type="date" id="jrny_date" name="jrny_date" placeholder="jounery date.." />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-25">
                                            <label for="jrny_from">Journey From</label>
                                        </div>
                                        <div className="col-75">
