import React from 'react';
import { useSelector } from 'react-redux';
import './Account.css';

function Account() {
  const transactions = useSelector((state) => state.transactions);
  const users = useSelector((state) => state.users);
  const [selectedUser, setSelectedUser] = React.useState(users.length > 0 ? users[0].id : null);

  return (
    <div className="account-page">
      <h1 className="account-heading">Account Heading</h1>
      <p className="account-tagline">Your random tagline here</p>
      <div className="info-boxes">
        <div className="info-box">
          <div className="info-row">
            Total Balance:{' '}
            <span className="balance-value">
              {selectedUser ? users.find((user) => user.id === selectedUser).balance : 'N/A'}
            </span>
          </div>
          <div className="info-row">
            Total Tokens:{' '}
            <span className="token-value">
              {selectedUser ? JSON.stringify(users.find((user) => user.id === selectedUser).tokens) : 'N/A'}
            </span>
          </div>
          <div className="info-row">
            <select onChange={(e) => setSelectedUser(Number(e.target.value))}>
              <option value="">Select User</option>
              {users.map((user) => (
                <option key={user.id} value={user.id}>
                  {user.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>
      <nav className="navigation-bar">
        <a href="#">Transactions</a>
      </nav>
      <div className="transactions-table">
        <table>
          <thead>
            <tr>
              <th>User ID</th>
              <th>Type</th>
              <th>Stock</th>
              <th>Amount</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((tx, index) => (
              <tr key={index}>
                <td>{tx.userId}</td>
                <td>{tx.type}</td>
                <td>{tx.stock}</td>
                <td>{tx.amount}</td>
                <td>{new Date(tx.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Account;
