import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'
import './Account.css'

function Account() {
  const transactions = useSelector((state) => state.transactions)
  const adminTransactions = useSelector((state) => state.adminTransactions)
  const adminHoldings = useSelector((state) => state.adminHoldings) || []
  const adminFunds = useSelector((state) => state.AdminFunds) || 1000000

  const [selectedUser, setSelectedUser] = useState(1) // Assuming default selected user
  const [tokenTransactions, setTokenTransactions] = useState([])

  useEffect(() => {
    // Filter out token transactions from all transactions
    const filteredTokenTransactions = transactions.filter(
      (tx) => tx.type === 'Buy Token'
    )
    setTokenTransactions(filteredTokenTransactions)
  }, [transactions])

  const formatTimestamp = (timestamp) => {
    return new Intl.DateTimeFormat('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false,
      timeZone: 'Asia/Kolkata',
    }).format(new Date(timestamp))
  }

  // Update admin holdings based on admin transactions
  useEffect(() => {
    adminTransactions.forEach((tx) => {
      if (tx.type === 'Admin Buy Stock') {
        // Check if stock is already in admin holdings
        const existingHolding = adminHoldings.find(
          (holding) => holding.stock === tx.stock
        )
        if (existingHolding) {
          // Update existing holding
          existingHolding.quantity += tx.amount
        } else {
          // Add new holding
          adminHoldings.push({
            stock: tx.stock,
            quantity: tx.amount,
          })
        }
      } else if (tx.type === 'Admin Sell Stock') {
        // Find the holding for the sold stock
        const holdingIndex = adminHoldings.findIndex(
          (holding) => holding.stock === tx.stock
        )
        if (holdingIndex !== -1) {
          // Reduce the quantity from holdings
          adminHoldings[holdingIndex].quantity -= tx.amount
          // If quantity becomes zero or less, remove it from holdings
          if (adminHoldings[holdingIndex].quantity <= 0) {
            adminHoldings.splice(holdingIndex, 1)
          }
        }
      }
    })
    // Ensure state update triggers re-render
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [adminTransactions])

  return (
    <div className="account-page">
      <h1 className="account-heading">Account Heading</h1>
      <p className="account-tagline">Your random tagline here</p>

      <div className="admin-section">
        <div className="funds-table">
          <table>
            <thead>
              <tr>
                <th colSpan="2">Funds Info</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Total Balance</td>
                <td>{adminFunds}</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="holdings-table">
          <table>
            <thead>
              <tr>
                <th>Stock</th>
                <th>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {adminHoldings.map((holding, index) => (
                <tr key={index}>
                  <td>{holding.stock}</td>
                  <td>{holding.quantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="transactions-section">
        <h2>Token Transactions</h2>
        <div className="transactions-table">
          <table>
            <thead>
              <tr>
                <th>User ID</th>
                <th>Type</th>
                <th>Stock</th>
                <th>Amount</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {tokenTransactions.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.userId}</td>
                  <td>{tx.type}</td>
                  <td>{tx.stock}</td>
                  <td>{tx.amount}</td>
                  <td>{formatTimestamp(tx.timestamp)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}

export default Account
