import React, { useState, useEffect, useRef } from 'react'
import { Line } from 'react-chartjs-2'
import 'chart.js/auto'
import './Stock.css'

const initialStocks = [
  { name: 'AAPL', price: Math.floor(Math.random() * 1000) },
  { name: 'GOOGL', price: Math.floor(Math.random() * 1000) },
  { name: 'AMZN', price: Math.floor(Math.random() * 1000) },
  { name: 'MSFT', price: Math.floor(Math.random() * 1000) },
  { name: 'TSLA', price: Math.floor(Math.random() * 1000) },
]

const strategies = {
  AAPL: (price) => price * (1 + (Math.random() * 0.02 - 0.01)),
  GOOGL: (price) =>
    price *
    (1 + 0.01 * Math.sin(Date.now() / 1000) * (Math.random() > 0.5 ? 1 : -1)),
  AMZN: (price) => price * (1 + (Math.random() > 0.5 ? 0.015 : -0.015)),
  MSFT: (price) =>
    price *
    (1 + 0.01 * Math.cos(Date.now() / 1000) * (Math.random() > 0.5 ? 1 : -1)),
  TSLA: (price) => price * (1 + (Math.random() * 0.03 - 0.015)),
}

const Stock = () => {
  const [stocks, setStocks] = useState(initialStocks)
  const [selectedStock, setSelectedStock] = useState(initialStocks[0].name)
  const [chartData, setChartData] = useState({
    labels: Array.from({ length: 50 }, (_, i) => i),
    datasets: initialStocks.map((stock) => ({
      label: stock.name,
      data: Array(50).fill(stock.price),
      borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
      fill: false,
      pointBackgroundColor: stock.price >= stock.price ? 'green' : 'red',
    })),
  })
  const [baselines, setBaselines] = useState(
    initialStocks.reduce(
      (acc, stock) => ({ ...acc, [stock.name]: stock.price }),
      {}
    )
  )
  const priceHistory = useRef(
    initialStocks.reduce(
      (acc, stock) => ({ ...acc, [stock.name]: [stock.price] }),
      {}
    )
  )

  useEffect(() => {
    const interval = setInterval(() => {
      setStocks((prevStocks) =>
        prevStocks.map((stock) => {
          const newPrice = strategies[stock.name](stock.price)
          return {
            ...stock,
            price: newPrice,
          }
        })
      )
    }, 1000)

    return () => clearInterval(interval)
  }, [])

  useEffect(() => {
    const updateBaselines = () => {
      setBaselines((prevBaselines) =>
        stocks.reduce((acc, stock) => {
          const history = priceHistory.current[stock.name]
          const avgPrice =
            history.reduce((sum, price) => sum + price, 0) / history.length
          return { ...acc, [stock.name]: avgPrice }
        }, {})
      )
    }

    const interval = setInterval(() => {
      stocks.forEach((stock) => {
        priceHistory.current[stock.name].push(stock.price)
        if (priceHistory.current[stock.name].length > 120) {
          priceHistory.current[stock.name].shift()
        }
      })
      updateBaselines()
    }, 2000)

    return () => clearInterval(interval)
  }, [stocks])

  useEffect(() => {
    setChartData((prevData) => ({
      ...prevData,
      datasets: prevData.datasets.map((dataset, index) => {
        const stock = stocks.find((stock) => stock.name === dataset.label)
        dataset.data.push(stock.price)
        if (dataset.data.length > 50) dataset.data.shift()
        dataset.pointBackgroundColor =
          stock.price >= baselines[stock.name] ? 'green' : 'red'
        return dataset
      }),
    }))
  }, [stocks, baselines])

  const handleStockSelect = (stockName) => {
    setSelectedStock(stockName)
  }

  const selectedDataset = chartData.datasets.find(
    (dataset) => dataset.label === selectedStock
  )

  return (
    <div className="stock-container">
      <h1>Stock Trading Platform</h1>
      <div className="main-content">
        <div className="stocks-list">
          {stocks.map((stock, index) => (
            <div
              key={stock.name}
              className={`stock-item ${
                stock.name === selectedStock ? 'selected' : ''
              }`}
              onClick={() => handleStockSelect(stock.name)}
            >
              <span>{stock.name}</span>
              <span
                className={`price ${
                  stock.price >= baselines[stock.name]
                    ? 'price-up'
                    : 'price-down'
                }`}
              >
                {stock.price.toFixed(2)}
              </span>
            </div>
          ))}
        </div>
        <div className="chart-container">
          {selectedDataset && (
            <Line
              data={{
                labels: chartData.labels,
                datasets: [selectedDataset],
              }}
            />
          )}
        </div>
      </div>
    </div>
  )
}

export default Stock
