import React, { useState, useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './Stock.css';

const initialStocks = [
  { name: 'AAPL', price: Math.floor(Math.random() * 1000) },
  { name: 'GOOGL', price: Math.floor(Math.random() * 1000) },
  { name: 'AMZN', price: Math.floor(Math.random() * 1000) },
  { name: 'MSFT', price: Math.floor(Math.random() * 1000) },
  { name: 'TSLA', price: Math.floor(Math.random() * 1000) },
];

const strategies = {
  AAPL: (price) => price * (1 + (Math.random() * 0.02 - 0.01)),
  GOOGL: (price) => price * (1 + 0.01 * Math.sin(Date.now() / 1000) * (Math.random() > 0.5 ? 1 : -1)),
  AMZN: (price) => price * (1 + (Math.random() > 0.5 ? 0.015 : -0.015)),
  MSFT: (price) => price * (1 + 0.01 * Math.cos(Date.now() / 1000) * (Math.random() > 0.5 ? 1 : -1)),
  TSLA: (price) => price * (1 + (Math.random() * 0.03 - 0.015)),
};

const Stock = () => {
  const [stocks, setStocks] = useState(initialStocks);
  const [selectedStock, setSelectedStock] = useState(initialStocks[0].name);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: initialStocks.map(stock => ({
      label: stock.name,
      data: [],
      borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
      fill: false,
      pointRadius: 0,
    })),
  });
  const [baselines, setBaselines] = useState(
    initialStocks.reduce((acc, stock) => ({ ...acc, [stock.name]: stock.price }), {})
  );
  const [timeSpan, setTimeSpan] = useState(60); // Default to 60 minutes
  const chartRef = useRef(null);
  const priceHistory = useRef(initialStocks.reduce((acc, stock) => ({ ...acc, [stock.name]: [] }), {}));

  useEffect(() => {
    const updateChartData = () => {
      setChartData(prevData => {
        const labels = Array.from({ length: prevData.labels.length + 1 }, (_, i) => i);
        const datasets = prevData.datasets.map(dataset => {
          const stock = stocks.find(stock => stock.name === dataset.label);
          return {
            ...dataset,
            data: [...dataset.data, stock.price],
          };
        });
        return { labels, datasets };
      });
    };

    const interval = setInterval(() => {
      setStocks(prevStocks =>
        prevStocks.map(stock => {
          const newPrice = strategies[stock.name](stock.price);
          priceHistory.current[stock.name].push(newPrice);
          if (priceHistory.current[stock.name].length > timeSpan * 60) {
            priceHistory.current[stock.name].shift();
          }
          return {
            ...stock,
            price: newPrice,
          };
        })
      );
    }, 1000);

    return () => clearInterval(interval);
  }, [timeSpan]);

  useEffect(() => {
    const updateBaselines = () => {
      setBaselines(prevBaselines =>
        stocks.reduce((acc, stock) => {
          const history = priceHistory.current[stock.name];
          const avgPrice = history.reduce((sum, price) => sum + price, 0) / history.length;
          return { ...acc, [stock.name]: avgPrice };
        }, {})
      );
    };

    const interval = setInterval(() => {
      updateBaselines();
    }, 1000);

    return () => clearInterval(interval);
  }, [stocks]);

  useEffect(() => {
    // Initialize chart with initial data
    initialStocks.forEach(stock => {
      priceHistory.current[stock.name] = Array(timeSpan * 60).fill(stock.price);
    });

    // Set initial labels based on time span
    const labels = Array.from({ length: timeSpan * 60 }, (_, i) => i);
    setChartData(prevData => ({
      ...prevData,
      labels,
    }));

    // Scroll chart to the right to show recent data
    if (chartRef.current) {
      chartRef.current.chartInstance.data.labels = labels;
      chartRef.current.chartInstance.update('none');
      const chartContainer = chartRef.current.chartInstance.chart.canvas.parentNode;
      chartContainer.scrollLeft = chartContainer.scrollWidth - chartContainer.clientWidth;
    }
  }, [timeSpan]);

  const handleStockSelect = stockName => {
    setSelectedStock(stockName);
  };

  const selectedDataset = chartData.datasets.find(dataset => dataset.label === selectedStock);
  const baselineDataset = {
    label: 'Base Line',
    data: selectedDataset ? Array(selectedDataset.data.length).fill(baselines[selectedStock]) : [],
    borderColor: 'rgba(0, 0, 0, 0.4)',
    borderWidth: 2,
    borderDash: [5, 5],
    fill: false,
    pointRadius: 0,
  };

  const stockMovementColor = (currentPrice, initialPrice) => {
    if (currentPrice > initialPrice) {
      return 'green';
    } else if (currentPrice < initialPrice) {
      return 'red';
    } else {
      return 'black';
    }
  };

  return (
    <div className="stock-container">
      <h1>Stock Trading Platform</h1>
      <div className="main-content">
        <div className="stocks-list">
          {stocks.map(stock => (
            <div
              key={stock.name}
              className={`stock-item ${stockMovementColor(stock.price, stock.initialPrice)}`}
              onClick={() => handleStockSelect(stock.name)}
            >
              <span>{stock.name}</span>
              <span className="price">{stock.price.toFixed(2)}</span>
            </div>
          ))}
        </div>
        <div className="chart-container">
          <div className="chart-wrapper">
            <div className="chart-scroll" ref={chartRef}>
              {selectedDataset && (
                <Line data={{
                  labels: chartData.labels,
                  datasets: [selectedDataset, baselineDataset],
                }} options={{
                  scales: {
                    x: {
                      type: 'linear',
                      position: 'bottom',
                      ticks: {
                        autoSkip: true,
                        maxTicksLimit: 10
                      }
                    },
                    y: {
                      beginAtZero: false,
                    }
                  },
                }} />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Stock;
