import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { buyToken, buyStockWithToken } from './redux/Store'
import './Users.css'

function Users() {
  const dispatch = useDispatch()
  const users = useSelector((state) => state.users)
  const stockPrices = useSelector((state) => state.stockPrices) // Accessing stockPrices from the Redux store
  const [selectedStock, setSelectedStock] = useState('Apple')
  const [tokenAmount, setTokenAmount] = useState(0)
  const [stockAmount, setStockAmount] = useState(0)
  const [popupMessages, setPopupMessages] = useState([])

  // Assuming you're only using the first user
  const user = users[0]

  const showPopups = (messages) => {
    setPopupMessages(messages)
    setTimeout(() => setPopupMessages([]), 3000) // Hide popups after 3 seconds
  }

  const handleBuyToken = () => {
    if (user && user.balance >= tokenAmount) {
      dispatch(
        buyToken({
          userId: user.id,
          stock: selectedStock,
          amount: Number(tokenAmount),
        })
      )
      showPopups([
        `User bought ${tokenAmount} Solana of ${selectedStock} stock.`,
        `Admin bought ${tokenAmount} token of ${selectedStock} stock.`,
      ])
      setTokenAmount(0) // Reset token amount after purchase
    } else {
      alert('Insufficient balance to buy tokens.')
    }
  }

  const handleBuyStockWithToken = () => {
    if (user) {
      const tokenBalance = user.tokens[selectedStock] || 0
      if (tokenBalance >= stockAmount) {
        dispatch(
          buyStockWithToken({
            userId: user.id,
            stock: selectedStock,
            amount: Number(stockAmount),
          })
        )
        showPopups([
          `User bought ${stockAmount} token of ${selectedStock} stock.`,
          `Admin sold ${stockAmount} token of ${selectedStock} stock.`,
        ])
        setStockAmount(0) // Reset stock amount after purchase
      } else {
        alert('Insufficient tokens to buy stock.')
      }
    }
  }

  return (
    <div className="users-page">
      {user && (
        <div className="user-box">
          <h2>{user.name}</h2>
          <p>Remaining Balance: {user.balance}</p>
          <div className="card-row">
            <div className="card">
              <h3>Solana Holdings</h3>
              <table>
                <thead>
                  <tr>
                    <th>Solana</th>
                    <th>Quantity</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(user.tokens).map(([token, quantity]) => (
                    <tr key={token}>
                      <td>{token}</td>
                      <td>{quantity}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="card">
              <h3>Stock Holdings</h3>
              <table>
                <thead>
                  <tr>
                    <th>Stock</th>
                    <th>Quantity</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(user.stocks).map(([stock, quantity]) => (
                    <tr key={stock}>
                      <td>{stock}</td>
                      <td>{quantity}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="card">
              <div className="buy-token-section">
                <h3>Buy Solana</h3>
                <select onChange={(e) => setSelectedStock(e.target.value)}>
                  {Object.keys(stockPrices).map((stock) => (
                    <option key={stock} value={stock}>
                      {stock} - ${stockPrices[stock]}
                    </option>
                  ))}
                </select>
                <input
                  type="number"
                  placeholder="Token Amount"
                  value={tokenAmount}
                  onChange={(e) => setTokenAmount(e.target.value)}
                />
                <button onClick={handleBuyToken}>Buy Solana</button>
              </div>
              <div className="buy-stock-section">
                <h3>Buy Stocks</h3>
                <select onChange={(e) => setSelectedStock(e.target.value)}>
                  {Object.keys(stockPrices).map((stock) => (
                    <option key={stock} value={stock}>
                      {stock} - ${stockPrices[stock]}
                    </option>
                  ))}
                </select>
                <input
                  type="number"
                  placeholder="Stock Amount"
                  value={stockAmount}
                  onChange={(e) => setStockAmount(e.target.value)}
                />
                <button onClick={handleBuyStockWithToken}>
                  Buy Stock with Solana
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      {popupMessages.map((message, index) => (
        <div key={index} className="popup">
          {message}
        </div>
      ))}
    </div>
  )
}

export default Users
