import React from 'react'
import { useSelector } from 'react-redux'
import './Observer.css'
import Trading_Image from '../Utility/Trading_Image.png'

function Observer() {
  const transactions = useSelector((state) => state.transactions)
  const stocks = useSelector((state) => state.stocks)
  const users = useSelector((state) => state.users)

  // Filter transactions for stock buys and sells
  const stockTransactions = transactions.filter(
    (tx) => tx.type === 'Buy Stock' || tx.type === 'Sell Stock'
  )

  const getBuyerType = (userId) => {
    if (userId === 'admin') {
      return 'Admin'
    } else {
      const user = users.find((user) => user.id === userId)
      if (user) {
        return `User${user.id}`
      }
    }
    return ''
  }

  return (
    <div className="observer-page">
      <div className="observer-content">
        <img className="observer-image" src={Trading_Image} alt="Observer" />
      </div>
      <div className="transactions-table">
        <h2>Observer Transactions (Stock Buy/Sell)</h2>
        <table>
          <thead>
            <tr>
              <th>User ID</th>
              <th>Buyer Type</th>
              <th>Type</th>
              <th>Stock</th>
              <th>Amount</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {stockTransactions.map((tx, index) => (
              <tr key={index}>
                <td>{tx.userId}</td>
                <td>{getBuyerType(tx.userId)}</td>
                <td>{tx.type}</td>
                <td>{tx.stock}</td>
                <td>{tx.amount}</td>
                <td>{tx.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  )
}

export default Observer
