import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'
import './Account.css'

function Account() {
  const transactions = useSelector((state) => state.transactions)
  const users = useSelector((state) => state.users)
  const adminHoldings = useSelector((state) => state.adminHoldings) || [] // Initialize as an empty array if undefined
  const adminFunds = useSelector((state) => state.AdminFunds) || 1000000 // Initialize AdminFunds with $1,000,000

  const [currentFunds, setCurrentFunds] = useState(adminFunds)
  const [profit, setProfit] = useState(0)
  const [profitPercent, setProfitPercent] = useState(0)
  const [loss, setLoss] = useState(0)
  const [lossPercent, setLossPercent] = useState(0)

  useEffect(() => {
    // Calculate profit and loss from admin transactions
    let totalProfit = 0
    let totalLoss = 0

    transactions.forEach((tx) => {
      if (tx.type === 'Admin Buy Stock') {
        // Calculate profit/loss based on previous buy price (if available)
        const previousBuyPrice =
          adminHoldings.find((holding) => holding.stock === tx.stock)?.price ||
          0
        const currentBuyPrice = tx.amount // Assuming tx.amount is the current buy price
        const profitLoss = currentBuyPrice - previousBuyPrice
        if (profitLoss > 0) {
          totalProfit += profitLoss
        } else {
          totalLoss += profitLoss
        }
      } else if (tx.type === 'Admin Sell Stock') {
        // Calculate profit/loss based on previous buy price (if available)
        const previousBuyPrice =
          adminHoldings.find((holding) => holding.stock === tx.stock)?.price ||
          0
        const currentSellPrice = tx.amount // Assuming tx.amount is the current sell price
        const profitLoss = currentSellPrice - previousBuyPrice
        if (profitLoss > 0) {
          totalProfit += profitLoss
        } else {
          totalLoss += profitLoss
        }
      }
    })

    // Update state with calculated profit and loss
    setProfit(totalProfit)
    setLoss(totalLoss)

    // Calculate profit percent and loss percent
    const totalInvestment = adminHoldings.reduce(
      (acc, holding) => acc + holding.quantity * holding.price,
      0
    )
    const totalGain = totalProfit
    const totalLossAbs = Math.abs(totalLoss)
    const profitPercent = ((totalGain / totalInvestment) * 100).toFixed(2)
    const lossPercent = ((totalLossAbs / totalInvestment) * 100).toFixed(2)

    // Update state with profit percent and loss percent
    setProfitPercent(profitPercent)
    setLossPercent(lossPercent)

    // Update current funds based on admin transactions
    const totalAdminFunds = adminFunds + totalProfit + totalLoss
    setCurrentFunds(totalAdminFunds)

    // Save updated AdminFunds to localStorage or dispatch to Redux store
    // Example: dispatch(updateAdminFunds(totalAdminFunds))
  }, [transactions, adminHoldings, adminFunds])

  const formatTimestamp = (timestamp) => {
    return new Intl.DateTimeFormat('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false,
      timeZone: 'Asia/Kolkata',
    }).format(new Date(timestamp))
  }

  return (
    <div className="account-page">
      <h1 className="account-heading">Account Heading</h1>
      <p className="account-tagline">Your random tagline here</p>

      <div className="admin-section">
        <div className="funds-table">
          <table>
            <thead>
              <tr>
                <th colSpan="2">Admin Funds</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Total Funds</td>
                <td>{adminFunds}</td>
              </tr>
              <tr>
                <td>Current Funds</td>
                <td>{currentFunds}</td>
              </tr>
              <tr>
                <td>Profit</td>
                <td>{profit}</td>
              </tr>
              <tr>
                <td>Profit %</td>
                <td>{profitPercent}%</td>
              </tr>
              <tr>
                <td>Loss</td>
                <td>{loss}</td>
              </tr>
              <tr>
                <td>Loss %</td>
                <td>{lossPercent}%</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="holdings-table">
          <table>
            <thead>
              <tr>
                <th>Stock</th>
                <th>Quantity</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {adminHoldings.map((holding, index) => (
                <tr key={index}>
                  <td>{holding.stock}</td>
                  <td>{holding.quantity}</td>
                  <td>{holding.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="transactions-section">
        <h2>Token Transactions</h2>
        <div className="transactions-table">
          <table>
            <thead>
              <tr>
                <th>User ID</th>
                <th>Type</th>
                <th>Amount</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.userId}</td>
                  <td>{tx.type}</td>
                  <td>{tx.amount}</td>
                  <td>{formatTimestamp(tx.timestamp)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}

export default Account
