import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import './Account.css';

function Account() {
  const transactions = useSelector((state) => state.transactions);
  const adminTransactions = useSelector((state) => state.adminTransactions);
  const adminFunds = useSelector((state) => state.AdminFunds) || 1000000;
  const stockPrices = useSelector((state) => state.stockPrices);

  const [adminHoldings, setAdminHoldings] = useState([]);
  const [tokenTransactions, setTokenTransactions] = useState([]);

  useEffect(() => {
    const filteredTokenTransactions = transactions.filter(
      (tx) => tx.type === 'Buy Token'
    );
    setTokenTransactions(filteredTokenTransactions);
  }, [transactions]);

  const formatTimestamp = (timestamp) => {
    return new Intl.DateTimeFormat('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false,
      timeZone: 'Asia/Kolkata',
    }).format(new Date(timestamp));
  };

  useEffect(() => {
    const holdings = [];

    adminTransactions.forEach((tx) => {
      if (tx.type === 'Admin Buy Stock') {
        const existingHolding = holdings.find(
          (holding) => holding.stock === tx.stock
        );
        if (existingHolding) {
          existingHolding.quantity += tx.amount;
        } else {
          holdings.push({
            stock: tx.stock,
            quantity: tx.amount,
          });
        }
      } else if (tx.type === 'Admin Sell Stock') {
        const holdingIndex = holdings.findIndex(
          (holding) => holding.stock === tx.stock
        );
        if (holdingIndex !== -1) {
          holdings[holdingIndex].quantity -= tx.amount;
          if (holdings[holdingIndex].quantity <= 0) {
            holdings.splice(holdingIndex, 1);
          }
        }
      }
    });

    setAdminHoldings(holdings);
  }, [adminTransactions]);

  return (
    <div className="account-page">
      <h1 className="account-heading">Account Heading</h1>
      <p className="account-tagline">Your random tagline here</p>

      <div className="admin-section">
        <div className="funds-table">
          <table>
            <thead>
              <tr>
                <th colSpan="2">Funds Info</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Total Balance</td>
                <td>{adminFunds} $</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="holdings-table">
          <table>
            <thead>
              <tr>
                <th>Stock</th>
                <th>Quantity</th>
                <th>Worth</th>
              </tr>
            </thead>
            <tbody>
              {adminHoldings.map((holding, index) => (
                <tr key={index}>
                  <td>{holding.stock}</td>
                  <td>{holding.quantity}</td>
                  <td>
                    {stockPrices[holding.stock]
                      ? (holding.quantity * stockPrices[holding.stock]).toFixed(2)
                      : 'Price not available'}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="transactions-section">
        <h2>Token Transactions</h2>
        <div className="transactions-table">
          <table>
            <thead>
              <tr>
                <th>User ID</th>
                <th>Type</th>
                <th>Stock</th>
                <th>Amount</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {tokenTransactions.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.userId}</td>
                  <td>{tx.type}</td>
                  <td>{tx.stock}</td>
                  <td>{tx.amount} $</td>
                  <td>{formatTimestamp(tx.timestamp)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Account;
