import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {
  addTransaction,
  updateAdminFunds,
  updateAdminStocks,
} from './redux/actions'
import './Users.css'

const stockList = ['Stock A', 'Stock B', 'Stock C', 'Stock D', 'Stock E']

function Users() {
  const dispatch = useDispatch()
  const transactions = useSelector((state) => state.transactions)
  const [users, setUsers] = useState(
    new Array(5).fill().map((_, index) => ({
      id: index + 1,
      tokens: {},
    }))
  )

  const handleBuyToken = (userId, stock, tokenAmount) => {
    const user = users.find((u) => u.id === userId)
    const updatedTokens = {
      ...user.tokens,
      [stock]: (user.tokens[stock] || 0) + tokenAmount,
    }
    const updatedUsers = users.map((u) =>
      u.id === userId ? { ...u, tokens: updatedTokens } : u
    )
    setUsers(updatedUsers)

    // Add transaction for user buying token
    const tokenTransaction = {
      userId,
      type: 'buy_token',
      stock,
      amount: tokenAmount,
      time: new Date().toISOString(),
      instructions: `User ${userId} bought ${tokenAmount} tokens of ${stock}`,
      signature: 'randomSignature',
      block: 'randomBlock',
      by: `User ${userId}`,
      fee: 'randomFee',
    }
    dispatch(addTransaction(tokenTransaction))

    // Admin buys the stock automatically
    const stockTransaction = {
      userId: 'admin',
      type: 'buy_stock',
      stock,
      amount: tokenAmount,
      time: new Date().toISOString(),
      instructions: `Admin bought ${tokenAmount} stocks of ${stock} as User ${userId} bought tokens`,
      signature: 'randomSignature',
      block: 'randomBlock',
      by: 'admin',
      fee: 'randomFee',
    }
    dispatch(addTransaction(stockTransaction))
    dispatch(updateAdminStocks({ stock, amount: tokenAmount }))
    dispatch(updateAdminFunds(-tokenAmount * 100)) // Assume stock price is 100 for simplicity

    alert('Token purchase and corresponding stock purchase recorded!')
  }

  const handleBuyStock = (userId, stock, stockAmount) => {
    const user = users.find((u) => u.id === userId)
    if (user.tokens[stock] >= stockAmount) {
      const updatedTokens = {
        ...user.tokens,
        [stock]: user.tokens[stock] - stockAmount,
      }
      const updatedUsers = users.map((u) =>
        u.id === userId ? { ...u, tokens: updatedTokens } : u
      )
      setUsers(updatedUsers)

      // Add transaction for user buying stock
      const stockTransaction = {
        userId,
        type: 'buy_stock',
        stock,
        amount: stockAmount,
        time: new Date().toISOString(),
        instructions: `User ${userId} bought ${stockAmount} stocks of ${stock} using tokens`,
        signature: 'randomSignature',
        block: 'randomBlock',
        by: `User ${userId}`,
        fee: 'randomFee',
      }
      dispatch(addTransaction(stockTransaction))

      // Admin sells the stock automatically
      const sellTransaction = {
        userId: 'admin',
        type: 'sell_stock',
        stock,
        amount: stockAmount,
        time: new Date().toISOString(),
        instructions: `Admin sold ${stockAmount} stocks of ${stock} as User ${userId} bought stocks using tokens`,
        signature: 'randomSignature',
        block: 'randomBlock',
        by: 'admin',
        fee: 'randomFee',
      }
      dispatch(addTransaction(sellTransaction))
      dispatch(updateAdminStocks({ stock, amount: -stockAmount }))
      dispatch(updateAdminFunds(stockAmount * 100)) // Assume stock price is 100 for simplicity

      alert('Stock purchase and corresponding stock sale recorded!')
    } else {
      alert('Not enough tokens to buy stock')
    }
  }

  return (
    <div className="users-page">
      {users.map((user) => (
        <div key={user.id} className="user-box">
          <h2>User {user.id}</h2>
          <select
            onChange={(e) => setSelectedStock(e.target.value)}
            value={selectedStock}
          >
            <option value="" disabled>
              Select Stock
            </option>
            {stockList.map((stock) => (
              <option key={stock} value={stock}>
                {stock}
              </option>
            ))}
          </select>
          <input
            type="number"
            placeholder="Token Amount"
            value={tokenAmount}
            onChange={(e) => setTokenAmount(e.target.value)}
          />
          <button
            onClick={() => handleBuyToken(user.id, selectedStock, tokenAmount)}
          >
            Buy Token
          </button>
          <button
            onClick={() => handleBuyStock(user.id, selectedStock, tokenAmount)}
          >
            Buy Stock Using Token
          </button>
          <div>Available Tokens: {JSON.stringify(user.tokens)}</div>
        </div>
      ))}
    </div>
  )
}

export default Users
