import React, { useState, useEffect, useRef } from 'react'
import { Line } from 'react-chartjs-2'
import 'chart.js/auto'
import './Stock.css'
import { Chart } from 'chart.js/auto'
const initialStocks = [
  { name: 'AAPL', price: Math.floor(Math.random() * 1000) },
  { name: 'GOOGL', price: Math.floor(Math.random() * 1000) },
  { name: 'AMZN', price: Math.floor(Math.random() * 1000) },
  { name: 'MSFT', price: Math.floor(Math.random() * 1000) },
  { name: 'TSLA', price: Math.floor(Math.random() * 1000) },
]

const strategies = {
  AAPL: (price) => price * (1 + Math.random() * 0.02 - 0.01),
  GOOGL: (price) => price * (1 + 0.005 * Math.sin(Date.now() / 1000)),
  AMZN: (price) => price * (1 + (Math.random() > 0.5 ? 0.015 : -0.015)),
  MSFT: (price) => price * (1 + 0.01 * Math.cos(Date.now() / 1000)),
  TSLA: (price) => price * (1 + (Math.random() * 0.03 - 0.015)),
}
ChartJS.register(ChartJS.scaleService.getScaleAdapter())
const Stock = () => {
  const [stocks, setStocks] = useState(initialStocks)
  const [selectedStock, setSelectedStock] = useState(initialStocks[0].name)
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: initialStocks[0].name,
        data: [],
        borderColor: 'green',
        fill: false,
      },
    ],
  })
  const prevPrices = useRef(initialStocks.map((stock) => stock.price))

  useEffect(() => {
    const interval = setInterval(() => {
      setStocks((prevStocks) =>
        prevStocks.map((stock) => {
          const newPrice = strategies[stock.name](stock.price)
          return {
            ...stock,
            price: newPrice,
          }
        })
      )
    }, 1000)

    return () => clearInterval(interval)
  }, [])

  useEffect(() => {
    const selectedStockData = chartData.datasets[0]
    const stockIndex = stocks.findIndex((stock) => stock.name === selectedStock)
    if (stockIndex !== -1) {
      const newPrice = stocks[stockIndex].price
      const newDataPoint = {
        x: Date.now(),
        y: newPrice,
      }
      selectedStockData.data.push(newDataPoint)
      if (selectedStockData.data.length > 50) selectedStockData.data.shift()
      setChartData({ ...chartData })
    }
  }, [stocks, selectedStock])

  const handleStockSelect = (stockName) => {
    setSelectedStock(stockName)
    setChartData({
      labels: [],
      datasets: [
        {
          label: stockName,
          data: [],
          borderColor: 'green',
          fill: false,
        },
      ],
    })
  }

  const chartOptions = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'second',
          displayFormats: {
            second: 'HH:mm:ss',
          },
        },
        ticks: {
          maxRotation: 0,
          minRotation: 0,
        },
      },
      y: {
        beginAtZero: false,
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  }

  return (
    <div className="stock-container">
      <h1>Stock Trading Platform</h1>
      <div className="main-content">
        <div className="stocks-list">
          {stocks.map((stock, index) => (
            <div
              key={stock.name}
              className={`stock-item ${
                stock.name === selectedStock ? 'selected' : ''
              }`}
              onClick={() => handleStockSelect(stock.name)}
            >
              <span>{stock.name}</span>
              <span
                className={`price ${
                  stock.price >= prevPrices.current[index]
                    ? 'price-up'
                    : 'price-down'
                }`}
              >
                {stock.price.toFixed(2)}
              </span>
              {(prevPrices.current[index] = stock.price)}
            </div>
          ))}
        </div>
        <div className="chart-container">
          <Line data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  )
}

export default Stock
