import React from 'react'
import { useSelector } from 'react-redux'
import './Account.css'

function Account() {
  const transactions = useSelector((state) => state.transactions)
  const users = useSelector((state) => state.users)
  const admin = useSelector((state) => state.admin) // Include admin state for funds
  const stocks = useSelector((state) => state.stocks)
  const [selectedUser, setSelectedUser] = React.useState(users[0].id)

  // Filter transactions for active tokens and consumed tokens
  const activeTokens = transactions.filter(
    (tx) =>
      tx.userId === selectedUser && tx.type === 'Buy Token' && !tx.consumed
  )

  const consumedTokens = transactions.filter(
    (tx) => tx.userId === selectedUser && tx.type === 'Buy Token' && tx.consumed
  )

  // Function to handle user stock purchase
  const handleBuyStock = (stock, amount) => {
    const user = users.find((user) => user.id === selectedUser)

    if (user.tokens[stock] >= amount) {
      // Dispatch action to buy stock with tokens
      dispatch(
        buyStockWithToken({
          userId: selectedUser,
          stock,
          amount,
        })
      )
    } else {
      alert(
        `Insufficient tokens (${user.tokens[stock]}) to buy ${amount} stocks.`
      )
    }
  }

  // Function to handle user token purchase
  const handleBuyToken = (stock, amount) => {
    const user = users.find((user) => user.id === selectedUser)

    if (user.balance >= amount) {
      // Dispatch action to buy token
      dispatch(
        buyToken({
          userId: selectedUser,
          stock,
          amount,
        })
      )
    } else {
      alert(`Insufficient funds (${user.balance}) to buy ${amount} tokens.`)
    }
  }

  return (
    <div className="account-page">
      <div className="admin-section">
        <div className="admin-table">
          <h2>Admin Stock Purchase History</h2>
          {/* Display admin's stock purchase history */}
        </div>
        <div className="admin-table">
          <h2>Admin Current Holdings</h2>
          <table>
            <thead>
              <tr>
                <th>Stock</th>
                <th>Tokens Held</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(stocks).map(([stock, tokens]) => (
                <tr key={stock}>
                  <td>{stock}</td>
                  <td>{tokens}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <div className="user-section">
        <h1 className="account-heading">Account Heading</h1>
        <p className="account-tagline">Your random tagline here</p>
        <div className="info-boxes">
          <div className="info-box">
            <div className="info-row">
              Total Balance:{' '}
              <span className="balance-value">
                {users.find((user) => user.id === selectedUser).balance}
              </span>
            </div>
            <div className="info-row">
              Total Tokens:{' '}
              <span className="token-value">
                {JSON.stringify(
                  users.find((user) => user.id === selectedUser).tokens
                )}
              </span>
            </div>
            <div className="info-row">
              <select onChange={(e) => setSelectedUser(Number(e.target.value))}>
                {users.map((user) => (
                  <option key={user.id} value={user.id}>
                    {user.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        <nav className="navigation-bar">
          <a href="#">Transactions</a>
        </nav>
        <div className="transactions-table">
          <h2>Active Tokens</h2>
          <table>
            <thead>
              <tr>
                <th>User ID</th>
                <th>Type</th>
                <th>Stock</th>
                <th>Amount</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {activeTokens.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.userId}</td>
                  <td>{tx.type}</td>
                  <td>{tx.stock}</td>
                  <td>{tx.amount}</td>
                  <td>{tx.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h2>Consumed Tokens</h2>
          <table>
            <thead>
              <tr>
                <th>User ID</th>
                <th>Type</th>
                <th>Stock</th>
                <th>Amount</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {consumedTokens.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.userId}</td>
                  <td>{tx.type}</td>
                  <td>{tx.stock}</td>
                  <td>{tx.amount}</td>
                  <td>{tx.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}

export default Account
