import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { buyToken } from './Store'
import './Users.css'

function Users() {
  const dispatch = useDispatch()
  const users = useSelector((state) => state.users)

  const handleBuyToken = (userId, stock, amount) => {
    dispatch(buyToken({ userId, stock, amount }))
  }

  return (
    <div className="users-page">
      {users.map((user) => (
        <div key={user.id} className="user-box">
          <h2>{user.name}</h2>
          <div className="buy-token-section">
            <select>
              {[
                'Stock1',
                'Stock2',
                'Stock3',
                'Stock4',
                'Stock5',
                'Stock6',
                'Stock7',
                'Stock8',
                'Stock9',
                'Stock10',
              ].map((stock) => (
                <option key={stock} value={stock}>
                  {stock}
                </option>
              ))}
            </select>
            <input type="number" placeholder="Amount" />
            <button onClick={() => handleBuyToken(user.id, 'Stock1', 100)}>
              Buy Token
            </button>
          </div>
        </div>
      ))}
    </div>
  )
}

export default Users
